import React, { useState } from 'react';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import { auth } from '../config/firebaseConfig'; // âœ… correct path

const RegisterForm = ({ onSwitchToLogin }) => {
  const [formData, setFormData] = useState({
    businessName: '',
    dbaName: '',
    owners: [''],
    coOwners: [''],
    email: '',
    confirmEmail: '',
    password: '',
    confirmPassword: '',
    phone: '',
    address: {
      street: '',
      country: '',
      state: '',
      city: '',
      pincode: '',
    },
    billingAddress: {
      street: '',
      country: '',
      state: '',
      city: '',
      pincode: '',
    },
    sameAsAddress: false,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.includes('owner')) {
      const updated = [...formData.owners];
      updated[parseInt(name.split('-')[1])] = value;
      setFormData({ ...formData, owners: updated });
    } else if (name.includes('coOwner')) {
      const updated = [...formData.coOwners];
      updated[parseInt(name.split('-')[1])] = value;
      setFormData({ ...formData, coOwners: updated });
    } else if (name.startsWith('billing')) {
      setFormData({
        ...formData,
        billingAddress: {
          ...formData.billingAddress,
          [name.replace('billing-', '')]: value,
        },
      });
    } else if (name in formData.address) {
      setFormData({
        ...formData,
        address: {
          ...formData.address,
          [name]: value,
        },
      });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const addOwner = () => setFormData({ ...formData, owners: [...formData.owners, ''] });
  const addCoOwner = () => setFormData({ ...formData, coOwners: [...formData.coOwners, ''] });

  const handleCheckbox = () => {
    const same = !formData.sameAsAddress;
    setFormData({
      ...formData,
      sameAsAddress: same,
      billingAddress: same ? { ...formData.address } : { street: '', country: '', state: '', city: '', pincode: '' },
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { email, confirmEmail, password, confirmPassword } = formData;
    if (email !== confirmEmail || password !== confirmPassword) {
      alert('Email or password mismatch');
      return;
    }
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      alert('Registration successful. Now verify OTP.');
      // redirect to OTP verification page here
    } catch (error) {
      alert(error.message);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="businessName" value={formData.businessName} onChange={handleChange} placeholder="Business Name" />
      <input name="dbaName" value={formData.dbaName} onChange={handleChange} placeholder="DBA Name" />
      {formData.owners.map((owner, i) => (
        <input key={i} name={`owner-${i}`} value={owner} onChange={handleChange} placeholder={`Owner Name ${i + 1}`} />
      ))}
      <button type="button" onClick={addOwner}>+ Add Owner</button>
      {formData.coOwners.map((coOwner, i) => (
        <input key={i} name={`coOwner-${i}`} value={coOwner} onChange={handleChange} placeholder={`Co-Owner Name ${i + 1}`} />
      ))}
      <button type="button" onClick={addCoOwner}>+ Add Co-Owner</button>
      <input name="email" value={formData.email} onChange={handleChange} placeholder="Email" />
      <input name="confirmEmail" value={formData.confirmEmail} onChange={handleChange} placeholder="Confirm Email" />
      {formData.email && formData.confirmEmail && formData.email === formData.confirmEmail && <p style={{ color: 'green' }}>Email matched</p>}
      <input type="password" name="password" value={formData.password} onChange={handleChange} placeholder="Password" />
      <input type="password" name="confirmPassword" value={formData.confirmPassword} onChange={handleChange} placeholder="Confirm Password" />
      {formData.password && formData.confirmPassword && formData.password === formData.confirmPassword && <p style={{ color: 'green' }}>Password matched</p>}
      <input name="phone" value={formData.phone} onChange={handleChange} placeholder="Phone Number" />
      <input name="street" value={formData.address.street} onChange={handleChange} placeholder="Street" />
      <input name="country" value={formData.address.country} onChange={handleChange} placeholder="Country" />
      <input name="state" value={formData.address.state} onChange={handleChange} placeholder="State" />
      <input name="city" value={formData.address.city} onChange={handleChange} placeholder="City" />
      <input name="pincode" value={formData.address.pincode} onChange={handleChange} placeholder="Pincode" />

      <label><input type="checkbox" checked={formData.sameAsAddress} onChange={handleCheckbox} /> Same as Physical Address</label>
      {!formData.sameAsAddress && (
        <>
          <input name="billing-street" value={formData.billingAddress.street} onChange={handleChange} placeholder="Billing Street" />
          <input name="billing-country" value={formData.billingAddress.country} onChange={handleChange} placeholder="Billing Country" />
          <input name="billing-state" value={formData.billingAddress.state} onChange={handleChange} placeholder="Billing State" />
          <input name="billing-city" value={formData.billingAddress.city} onChange={handleChange} placeholder="Billing City" />
          <input name="billing-pincode" value={formData.billingAddress.pincode} onChange={handleChange} placeholder="Billing Pincode" />
        </>
      )}
      <button type="submit">Register</button>
      <p>Already have an account? <button type="button" onClick={onSwitchToLogin}>Login</button></p>
    </form>
  );
};

export default RegisterForm;
